package controller;import domain.Stake;import exception.BusinessException;import http.annotation.*;import manager.SessionManager;import java.util.concurrent.ConcurrentHashMap;import java.util.stream.Collectors;/** * @author zhangshu * @ClassName SessionController * @description: SessionController * @date 2025年04月30日 * @version: 1.0 */@Controllerpublic class HomeWorkController {    @GetMapping("/{customerid}/session")    public String getSessionKey(@RequestParam("customerid") int customerid) {        return  SessionManager.getInstance().getSessionKey(customerid);    }    @PostMapping("/{betofferid}/stake")    public void stakeOnBetOffer(@RequestParam("betofferid") int betofferid,@RequestParam("sessionkey") String sessionKey,@RequestBody int stake) throws BusinessException {            Integer customerId = SessionManager.getInstance().invalidateSession(sessionKey);            if (customerId == null) {                throw  new BusinessException("已过期，请重新登录");            }        Stake.placeStake(betofferid,customerId,stake);    }    @GetMapping("/{betofferid}/highstakes")    public String getHighStakes(@RequestParam("betofferid") int betofferid) throws BusinessException {       return null;    }}